body.script:
function readURL(input) {
if (input.files && input.files[0]) {
var reader = new FileReader();

reader.onload = function (e) {
$('.image-upload-wrap').hide();
$('footer').hide();

$('.file-upload-image').attr('src', e.target.result);
$('.file-upload-content').show();

$('.image-title').html(input.files[0].name);
};

reader.readAsDataURL(input.files[0]);
init().then(() => {
predict();
$('#loading').hide();
});
} else {
removeUpload();
}
}

function removeUpload() {
$('.file-upload-input').replaceWith($('.file-upload-input').clone());
$('.file-upload-content').hide();
$('.image-upload-wrap').show();
}
$('.image-upload-wrap').bind('dragover', function () {
$('.image-upload-wrap').addClass('image-dropping');
});
$('.image-upload-wrap').bind('dragleave', function () {
$('.image-upload-wrap').removeClass('image-dropping');
});

// More API functions here:
// https://github.com/googlecreativelab/teachablemachine-community/tree/master/libraries/image

// the link to your model provided by Teachable Machine export panel
let URL;
const urlMale = 'https://teachablemachine.withgoogle.com/models/34hNLvH-t/';
const urlFemale = 'https://teachablemachine.withgoogle.com/models/slnAAex_6/';
let model, webcam, labelContainer, maxPredictions;

// Load the image model and setup the webcam
async function init() {
if(document.getElementById("gender").checked) {
URL = urlMale;
} else {
URL = urlFemale;
}
const modelURL = URL + 'model.json';
const metadataURL = URL + 'metadata.json';

// load the model and metadata
// Refer to tmImage.loadFromFiles() in the API to support files from a file picker
// or files from your local hard drive
// Note: the pose library adds "tmImage" object to your window (window.tmImage)
model = await tmImage.load(modelURL, metadataURL);
maxPredictions = model.getTotalClasses();
labelContainer = document.getElementById('label-container');
for (let i = 0; i < 5; i++) {
// and class labels
var element = document.createElement('div');
labelContainer.appendChild(element);
}
}

// run the webcam image through the image model
async function predict() {
// predict can take in an image, video or canvas html element
var image = document.getElementById('face-image');
const prediction = await model.predict(image, false);
prediction.sort((a, b) => parseFloat(b.probability) - parseFloat(a.probability));
console.log(prediction[0].className);
var result, resultSentence;
result = prediction[0].className;
resultSentence = '당신은 ' + result + '님과 패션 스타일이 비슷합니다!';
var celebName = "
" + result + '';
var sentence = '
' + resultSentence + '';

$('.result-message').html(celebName + sentence);
var barWidth;
for (let i = 0; i < maxPredictions; i++) {
if (prediction[i].probability.toFixed(2) >= 0.1) {
barWidth = Math.round(prediction[i].probability.toFixed(2) * 100) + '%';
} else if (prediction[i].probability.toFixed(2) >= 0.01) {
barWidth = '5%';
} else {
barWidth = '2%';
}
var labelName;
labelName = prediction[i].className;
switch(labelName){
case "방탄소년단 랩몬스터" : {
labelName = "랩몬스터";
break;
}
case "뉴이스트 황민현" : {
labelName = "황민현";
break;
}
case "방탄소년단 뷔" : {
labelName = "방탄소년단 뷔";
break;
}
case "블락비 피오" : {
labelName = "피오";
break;
}
case "레드벨벳 슬기" : {
labelName = "슬기";
break;
}
case "블랙핑크 리사" : {
labelName = "리사";
break;
}
case "블랙핑크 제니" : {
labelName = "제니";
break;
}
case "소녀시대 수영" : {
labelName = "수영";
break;
}
case "위키미키 김도연" : {
labelName = "김도연";
break;
}
case "티아라 지연" : {
labelName = "지연";
break;
}
default:
break;
}

var label =
"
" + labelName + '';
var bar =
"


" +
Math.round(prediction[i].probability.toFixed(2) * 100) +
'%';
labelContainer.childNodes[i].innerHTML = label + bar;
}
}
